mapper.update:
UPDATE NOTICE_CATE
SET
CATE_NAME = #{cateName}
WHERE CATE_NO = #{cateNo}

UPDATE ITEM_CATE
SET
CATE_NAME = #{cateName}
WHERE CATE_NO = #{cateNo}

UPDATE POS_CATE
SET
CATE_NAME = #{cateName}
WHERE CATE_NO = #{cateNo}

mapper.select:
SELECT RNUM, BRCH_NAME, ENROLL_DATE, SALES_PRICE, ORD_PRICE, NET_PRICE FROM (SELECT ROW_NUMBER() OVER(ORDER BY ENROLL_DATE DESC) AS RNUM, BRCH_NAME, ENROLL_DATE, SALES_PRICE, ORD_PRICE, (SALES_PRICE-ORD_PRICE) AS NET_PRICE FROM(SELECT NVL(O.BRCH_NAME,S.BRCH_NAME) AS BRCH_NAME, NVL(TO_CHAR(O.ENROLL_DATE,'YYYY-MM-DD'),TO_CHAR(S.ENROLL_DATE,'YYYY-MM-DD'))AS ENROLL_DATE, NVL(ORD_PRICE,0) AS ORD_PRICE, NVL(SALES_PRICE,0) AS SALES_PRICE FROM (SELECT B.BRCH_NAME, O.ENROLL_DATE, SUM(I.PRICE) AS ORD_PRICE FROM ORD O INNER JOIN ORD_ITEM I ON O.ORD_NO = I.ORD_NO INNER JOIN BRCH B ON O.BRCH_NO = B.BRCH_NO WHERE (O.STAT_CODE = 0 OR O.STAT_CODE = 2 OR O.STAT_CODE = 4 OR O.STAT_CODE = 5 OR O.STAT_CODE = 6 OR O.STAT_CODE = 8)
AND O.BRCH_NO = #{brch_choice}
AND O.BRCH_NO = 2
GROUP BY B.BRCH_NAME, O.ENROLL_DATE)O FULL OUTER JOIN (SELECT B.BRCH_NAME, S.ENROLL_DATE, SUM(M.CNT * P.MENU_PRICE) AS SALES_PRICE FROM SALES S INNER JOIN SALES_MENU M ON S.SALES_NO = M.SALES_NO INNER JOIN POS_MENU P ON M.MENU_NO = P.MENU_NO INNER JOIN BRCH B ON S.BRCH_NO = B.BRCH_NO WHERE S.CNL_DATE IS NULL
AND S.BRCH_NO = #{brch_choice}
AND S.BRCH_NO = 2
GROUP BY B.BRCH_NAME, S.ENROLL_DATE) S ON O.BRCH_NAME = S.BRCH_NAME AND O.ENROLL_DATE = S.ENROLL_DATE)
WHERE ENROLL_DATE BETWEEN TO_DATE(#{start_date}, 'YY/MM/DD') AND TO_DATE(#{end_date}, 'YY/MM/DD')
WHERE ENROLL_DATE >= TO_DATE(#{start_date}, 'YY/MM/DD')
WHERE ENROLL_DATE <= TO_DATE(#{end_date}, 'YY/MM/DD')
) WHERE RNUM BETWEEN #{startCnt} AND #{endCnt}
