mapper.select:
	SELECT COUNT(*) AS CNT
	FROM NOTICE N INNER JOIN HQ_USER U
	ON N.USER_NO = U.USER_NO
	WHERE N.DEL_FLAG = 1
    AND 1=1
	
	
		
		AND TITLE LIKE '%' || #{searchTxt} || '%'
		OR CONTENT LIKE '%' || #{searchTxt} || '%'
		
		
		AND TITLE LIKE '%' || #{searchTxt} || '%'
		
		
		AND CONTENT LIKE '%' || #{searchTxt} || '%'
		
		
		AND USER_NAME LIKE '%' || #{searchTxt} || '%'
		
	



         
            AND  CATE_NO IN (1,2,3,4,5)
         
         
            AND  CATE_NO IN #{cate}
         
         
            AND  CATE_NO IN #{cate}
         
         
            AND  CATE_NO IN #{cate}
         
         
            AND  CATE_NO IN #{cate}
         
         
            AND  CATE_NO IN #{cate}

SELECT N.TXT_NO, N.TITLE, N.S_DT, N.USER_NAME, N.DEL_FLAG, N.CATE_NO
FROM(SELECT TXT_NO, TITLE, TO_CHAR(WRITE_DATE, 'YYYY/MM/DD') AS S_DT,H.USER_NAME
        , ROW_NUMBER() OVER(ORDER BY TXT_NO DESC) AS RNUM, O.DEL_FLAG, O.CATE_NO
    FROM NOTICE O INNER JOIN HQ_USER H
                          ON O.USER_NO = H.USER_NO
WHERE O.DEL_FLAG=1 
AND 1=1
   
            
                   WHERE S_DT BETWEEN TO_DATE(#{start_date}, 'YY/MM/DD') AND TO_DATE(#{end_date},'YY/MM/DD') 
            
            
                   WHERE S_DT BETWEEN TO_DATE(#{start_date}, 'YY/MM/DD') AND TO_DATE(SYSDATE, 'YY/MM/DD') 
            
            
                   WHERE S_DT BETWEEN TO_DATE(S_DT, 'YY/MM/DD') AND TO_DATE(#{end_date}, 'YY/MM/DD') 
            
     
 
	
		
		AND TITLE LIKE '%' || #{searchTxt} || '%'
		OR CONTENT LIKE '%' || #{searchTxt} || '%'
		
		
		AND TITLE LIKE '%' || #{searchTxt} || '%'
		
		
		AND CONTENT LIKE '%' || #{searchTxt} || '%'
		
		
		AND USER_NAME LIKE '%' || #{searchTxt} || '%'
		
	



         
            AND  CATE_NO IN (0,1,2,3,4, 5)
         
         
            AND  CATE_NO IN #{cate}
         
         
            AND  CATE_NO IN #{cate}
         
         
            AND  CATE_NO IN #{cate}
         
         
            AND  CATE_NO IN #{cate}
         
         
            AND  CATE_NO IN #{cate}
         
         
      

)N
WHERE N.RNUM BETWEEN #{startCnt} AND #{endCnt}

SELECT U.USER_NO, U.NAME, U.USER_NAME, U.ID, U.DIVI, U.DEL_FLAG FROM(SELECT U.USER_NO AS USER_NO, U.NAME AS NAME, U.USER_NAME AS USER_NAME, U.ID AS ID, ROW_NUMBER() OVER(ORDER BY U.USER_NO DESC) AS RNUM, U.DIVI AS DIVI, U.DEL_FLAG AS DEL_FLAG FROM(SELECT U.USER_NO AS USER_NO, D.DEP_NAME AS NAME, U.USER_NAME AS USER_NAME, U.ID AS ID, U.DIVISION AS DIVI, U.DEL_FLAG AS DEL_FLAG FROM HQ_DEP D INNER JOIN HQ_USER U ON D.DEP_NO = U.DEP_NO UNION SELECT BRCH_NO AS USER_NO, BRCH_NAME AS NAME, MGR_NAME AS USER_NAME, ID, DIVISION AS DIVI, DEL_FLAG FROM BRCH) U WHERE DEL_FLAG = 1 AND 1=1
AND USER_NO LIKE '%' || #{searchTxt} || '%' OR NAME LIKE '%' || #{searchTxt} || '%' OR USER_NAME LIKE '%' || #{searchTxt} || '%' OR ID LIKE '%' || #{searchTxt} || '%'
AND USER_NO LIKE '%' || #{searchTxt} || '%'
AND NAME LIKE '%' || #{searchTxt} || '%'
AND USER_NAME LIKE '%' || #{searchTxt} || '%'
AND ID LIKE '%' || #{searchTxt} || '%'
AND DIVI = #{cateNo}
AND DIVI = #{cateNo}
AND DIVI IN (0,1)
)U WHERE U.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE BRCH SET ID = #{bId}, PW = #{bPw}, BRCH_NAME = #{bNm},
 POST_NUM = #{bPost}, DFT_ADDRESS = #{bDft}, DTL_ADDRESS = #{bDtl},
 CALL_NUM = #{bCall}, MGR_NAME = #{mgrNm}, MGR_PHONE_NUM = #{mgrPhone}
WHERE BRCH_NO IN #{uNo}
