mapper.select:
SELECT N.TXT_NO, N.TITLE, N.S_DT, N.USER_NAME AS USER_NO FROM(SELECT TXT_NO, TITLE, TO_CHAR(WRITE_DATE, 'YYYY/MM/DD') AS S_DT,H.USER_NAME , ROW_NUMBER() OVER(ORDER BY TXT_NO DESC) AS RNUM FROM NOTICE O INNER JOIN HQ_USER H ON O.USER_NO = H.USER_NO WHERE 1=1
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND CONTENT LIKE '%' || #{searchTxt} || '%'
AND USER_NO LIKE '%' || #{searchTxt} || '%'
)N WHERE N.RNUM BETWEEN #{startCnt} AND #{endCnt}
