mapper.select:
SELECT * FROM(SELECT ORD_NO, TO_CHAR(ORD_ENROLL_DATE, 'YYYY-MM-DD')AS ORD_ENROLL_DATE, BRCH_NAME, CODE_S_CATE, CODE_NAME, NON_APV_RSN, TO_CHAR(PROCESS_DATE, 'YYYY-MM-DD')AS PROCESS_DATE, ROW_NUMBER() OVER(ORDER BY ORD_NO DESC)AS RNUM FROM COMMON_CODE C INNER JOIN ORD O ON C.CODE_S_CATE=O.STAT_CODE INNER JOIN BRCH B ON B.BRCH_NO = O.BRCH_NO WHERE C.CODE_L_CATE=2 AND O.DEL_FLAG != 0
AND ORD_NO LIKE '%' || #{search_input} || '%'
)ORD WHERE ORD.RNUM BETWEEN #{startCnt} AND #{endCnt}
